import React from 'react';

const StatusMessage = ({ message }) => {
  if (!message) return null;

  return (
    <div
      style={{
        marginTop: '20px',
        padding: '10px',
        borderRadius: '5px',
        backgroundColor: message.startsWith('Error') ? '#ffcccc' : '#ccffcc',
        color: message.startsWith('Error') ? '#cc0000' : '#006600',
        textAlign: 'center',
      }}
    >
      <p>{message}</p>
    </div>
  );
};

export default StatusMessage;
