import React, { useState } from 'react';
import MeetingForm from './components/MeetingForm';
import StatusMessage from './components/StatusMessage';
import { joinMeeting } from './utils/api';

const App = () => {
  const [status, setStatus] = useState('');

  const handleJoinMeeting = async (link) => {
    setStatus('Joining the meeting...');
    try {
      const message = await joinMeeting(link);
      setStatus(message);
    } catch (error) {
      setStatus('Error: Could not join the meeting. Please check the link.');
    }
  };

  return (
    <div style={{ textAlign: 'center', padding: '2rem' }}>
      <h1>Meeting Bot</h1>
      <p>Paste your meeting link below to let the bot join the meeting.</p>
      <MeetingForm onSubmit={handleJoinMeeting} />
      <StatusMessage message={status} />
    </div>
  );
};

export default App;
