import React, { useState } from 'react';
import MeetingForm from './components/MeetingForm';
import StatusMessage from './components/StatusMessage';

const App = () => {
  const [status, setStatus] = useState('');

  const handleJoinMeeting = async (link) => {
    setStatus('Joining the meeting...');
    try {
      // Mocking the API response for now
      const message = `Bot has successfully joined the meeting: ${link}`;
      setStatus(message);
    } catch (error) {
      setStatus('Error: Could not join the meeting. Please check the link.');
    }
  };

  return (
    <div className="container">
      <h1>Meeting Bot</h1>
      <p>Paste your meeting link below to let the bot join the meeting.</p>
      <MeetingForm onSubmit={handleJoinMeeting} />
      {status && (
        <div
          className={`status-message ${
            status.startsWith('Error') ? 'error' : 'success'
          }`}
        >
          {status}
        </div>
      )}
    </div>
  );
};

export default App;
