import React, { useState } from 'react';
import MeetingForm from './components/MeetingForm';
import StatusMessage from './components/StatusMessage';

const App = () => {
  const [status, setStatus] = useState('');

  const handleJoinMeeting = async (link) => {
    setStatus('Joining the meeting...');
    try {
      const message = `Bot has successfully joined the meeting: ${link}`;
      setStatus(message);
    } catch (error) {
      setStatus('Error: Could not join the meeting. Please check the link.');
    }
  };

  return (
    <div
      style={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
      }}
    >
      {/* Background Image */}
      <div
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          backgroundImage: "url('/images/background.jpg')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          backgroundRepeat: "no-repeat",
          filter: "opacity(0.7)", // Adjust the transparency of the image
          zIndex: 0,
        }}
      ></div>

      {/* Content */}
      <div
        className="container"
        style={{
          position: "relative", // Ensure content is above the background
          zIndex: 2,
          backgroundColor: "rgba(255, 255, 255, )", // Slightly transparent white
          borderRadius: "10px",
          padding: "40px",
          maxWidth: "600px",
          margin: "100px auto",
          boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.25)",
          textAlign: "center",
        }}
      >
        <h1 style={{ fontSize: "2.5rem", fontWeight: "bold", color: "#333" }}>
          Meeting Bot
        </h1>
        <p style={{ fontSize: "1.2rem", color: "#555", marginBottom: "20px" }}>
          Paste your meeting link below to let the bot join the meeting.
        </p>
        <MeetingForm onSubmit={handleJoinMeeting} />
        <StatusMessage message={status} />
      </div>
    </div>
  );
};

export default App;
