import React, { useState } from 'react';
import MeetingForm from './components/MeetingForm';
import StatusMessage from './components/StatusMessage';

const App = () => {
  const [status, setStatus] = useState('');

  const handleJoinMeeting = async (link) => {
    setStatus('Joining the meeting...');
    try {
      // Mocking the API response for now
      const message = `Bot has successfully joined the meeting: ${link}`;
      setStatus(message);
    } catch (error) {
      setStatus('Error: Could not join the meeting. Please check the link.');
    }
  };

  return (
    <div
      style={{
        backgroundImage: "url('/images/background.jpg')", // Absolute path to the background image
        backgroundRepeat: "no-repeat",
        backgroundPosition: "right center",
        backgroundSize: "auto 100%", // Fit image from top to bottom
        minHeight: "100vh", // Full viewport height
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <div
        className="container"
        style={{
          backgroundColor: "rgba(255, 255, 255, 0.9)", // Semi-transparent white
          borderRadius: "10px",
          padding: "40px",
          maxWidth: "600px",
          boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.25)",
          textAlign: "center",
        }}
      >
        <h1 style={{ fontSize: "2.5rem", fontWeight: "bold", color: "#333" }}>
          Meeting Bot
        </h1>
        <p style={{ fontSize: "1.2rem", color: "#555", marginBottom: "20px" }}>
          Paste your meeting link below to let the bot join the meeting.
        </p>
        <MeetingForm onSubmit={handleJoinMeeting} />
        {status && (
          <div
            className={`status-message ${
              status.startsWith('Error') ? 'error' : 'success'
            }`}
            style={{
              marginTop: "20px",
              padding: "10px",
              borderRadius: "5px",
              textAlign: "center",
              backgroundColor: status.startsWith('Error')
                ? "#f8d7da"
                : "#d4edda",
              color: status.startsWith('Error') ? "#721c24" : "#155724",
            }}
          >
            {status}
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
