import React, { useState } from 'react';
import MeetingForm from './components/MeetingForm';
import StatusMessage from './components/StatusMessage';

const App = () => {
  const [status, setStatus] = useState('');

  const handleJoinMeeting = async (link) => {
    setStatus('Joining the meeting...');
    try {
      const message = `Bot has successfully joined the meeting: ${link}`;
      setStatus(message);
    } catch (error) {
      setStatus('Error: Could not join the meeting. Please check the link.');
    }
  };

  return (
    <div
      style={{
        position: "relative",
        minHeight: "100vh",
        backgroundImage: "url('/images/background.jpg')", // Use the correct path for your image in the public folder
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        backgroundAttachment: "fixed", // Ensures it remains fixed while scrolling
        filter: "brightness(85%)", // Reduces brightness for transparency effect
      }}
    >
      {/* Content */}
      <div
        className="container"
        style={{
          position: "relative",
          zIndex: 2,
          backgroundColor: "rgba(255, 255, 255, 0.9)", // Semi-transparent white
          borderRadius: "10px",
          padding: "40px",
          maxWidth: "600px",
          margin: "100px auto",
          boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.25)",
          textAlign: "center",
        }}
      >
        <h1 style={{ fontSize: "2.5rem", fontWeight: "bold", color: "#333" }}>
          Meeting Bot
        </h1>
        <p style={{ fontSize: "1.2rem", color: "#555", marginBottom: "20px" }}>
          Paste your meeting link below to let the bot join the meeting.
        </p>
        <MeetingForm onSubmit={handleJoinMeeting} />
        <StatusMessage message={status} />
      </div>
    </div>
  );
};

export default App;
